<template>
  <div class="countable" v-if="time">
    <p>{{ time }} min read</p>
  </div>
</template>

<script>
import Countable from "countable";

export default {
  data() {
    return {
      time: ""
    };
  },
  mounted() {
    const text = this.$page.excerpt;
    if (!text) return;
    Countable.count(text, counter => {
      this.time = Math.round(counter.words / 50);
    });
  }
};
</script>

<style scoped>
.countable {
  display: block;
}

p {
  display: block;
  color: #999;
  margin-top: -0.5rem;
}
</style>
